sys_variable_value.value:
(function execute(inputs, outputs) {
    /*var att = new GlideRecord('sys_attachment');
    att.addQuery('u_countries', "35334e45835dba10a14ac296feaad3f0");
    att.query();
    while (att.next()) {
        var attach = new GlideSysAttachment().getContentStream(att.sys_id.toString());
        var reader = new GlideTextReader(attach);
        var ln = ' ';
        while ((ln = reader.readLine()) != null) {
            outputs.attachment_content = ln;
        }
    }*/
    var attach = new GlideSysAttachment().getContentStream('35334e45835dba10a14ac296feaad3f0');
    var reader = new GlideTextReader(attach);
    var ln = ' ';
    while ((ln = reader.readLine()) != null) {
        outputs.attachment_content = ln;
    }
})(inputs, outputs);

