sys_variable_value.value:
(function execute(inputs, outputs) {
    var str = inputs.htmlSummaryEN;
    var decodedString = new GlideStringUtil().unEscapeHTML(str);
    outputs.decoded_html = decodedString;
})(inputs, outputs);

